 pragma solidity ^0.5.0;

import "./CaseContract.sol";

contract CaseFactory {
    event CaseContractCreated(address newCaseContract);
    address payable public manager;

    mapping(address => CaseContract) public cases;

    modifier managerOnly {
        require(msg.sender == manager, "forbidden");
        _;
    }

    constructor() public {
        manager = msg.sender;
    }

    function createCase(string memory _status) public managerOnly {
        CaseContract newCaseContract = new CaseContract(manager, _status);
        cases[address(newCaseContract)] = newCaseContract;
        emit CaseContractCreated(address(newCaseContract));
    }
}
